import { data } from "./Data/TodoListData"
import Task from "./Task";
import './Note.css';
import AddTodoItem from '../AddTodoItem'; // Import the new component   
import React, { useState } from 'react';

function Note() {
    const [todoList, setTodoList] = useState(data);

    const handleAddItem = (newItem) => {
         setTodoList([...todoList, { ...newItem, id: todoList.length + 1, description: { ...newItem.description, id: todoList.length + 1 } }]);
    };


    const sizeMax = {
        overflowY: 'auto', /* Add vertical scroll if content exceeds height */
        maxHeight: '700px', /* Set a maximum height for the container  */
        maxWidth: '650'
    }
    return (
        <>
            <div className="note-container ">
            <AddTodoItem onAddItem={handleAddItem} /> {/* Use the new component */}
            {todoList.map((task) => (
                <Task key={task.id} task={task} />
            ))}

                <div>
                    <div style={sizeMax}>
                        <Task />
                    </div>

                    <div className="agent" > 
                        <dev style={sizeMax}>
                            Agent
                        </dev>
                         
                    </div>

                </div>

                <div className="analysis" >
                    <div>
                        <p> analysis here </p>
                    </div>
                </div>

            </div>

        </>
    );
}

export default Note;
