import React, { useState } from 'react';
import { format } from 'date-fns';
import Form from './Form/Form';
import axios from 'axios';


function Detail({ id, task, todoList, setTodoList, HandleCancel, handleDetail, ShowDetail }) {
    // phần này đang sửa để kéo giá trị đã có từ db để người dùng sửa tiếp
    console.log('todoList: ', todoList)



    const onAddItem = async (newItem) => {

        axios.update('http://localhost:3006', newItem)
            .then(response => {
                setTodoList(prevTodoList => [...prevTodoList, {...newItem}]);
            })
        
    };

    console.log("task: " + task.due_date)
    const [newItem, setNewItem] = useState({
        created_at: new Date(task.created_at),
        due_date: new Date(task.due_date),
        title: task.title,
        content: task.content,
        status: task.status,
        rate_prosess: 0,
        actual_duration: 0,
    });
    const [errors, setErrors] = useState({});

// hàm dưới này dùng cách tạo hàm trống để bên form gọi và dùng chỉnh sửa tùy form và fetch api mai chỉnh lại cả đây và bên form
    const handleSubmit = (e, newItem) => {
        e.preventDefault();
        let isValid = true;
        if (isValid) {
            onAddItem(newItem);
            setNewItem({
                id: 0,
                due_date: newItem.due_date,
                created_at: newItem.created_at,
                title: '',
                content: '',
                status: newItem.status,
            });
        }
    };

    return (
        <>
            <Form newItem={newItem} handleSubmit={handleSubmit} id={id} task={task} setNewItem={setNewItem} HandleCancel={HandleCancel} handleSubmit={handleSubmit}/>
        </>
    );
}

export default Detail;


