import { useState, useEffect } from "react";
import AddTodoItem from "../../Note/AddTodoItem";
import "./AddTask.css"
import { FaCalendarPlus } from "react-icons/fa";

function AddTask({ handleItem }) {

    const [isAddingTask, setIsAddingTask] = useState(false);
    const [showAddTask, setShowAddTask] = useState(true)

    const handleAddTask = () => {
        setIsAddingTask(!isAddingTask)
        setShowAddTask(false)
    };


    return (
        <>
            <div className="add-task-container">

                {showAddTask && (
                    <button onClick={handleAddTask} className="add-task-button" style={{
                        width: '100%',
                        padding: '10px',
                        borderRadius: '5px',
                        backgroundColor: '#007bff',
                        color: 'white',
                        border: 'none',
                        cursor: 'pointer',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                    }}>
                        <FaCalendarPlus style={{fontSize: '1.5em'}}/>
                </button>)}

                {isAddingTask && (
                    <AddTodoItem onAddItem={handleItem} setcancelForm={setIsAddingTask} setAddTask={setShowAddTask} />
                )}
            </div>
        </>
    );
}

export default AddTask;
