import React, { useState } from 'react';
import "./Agent.css";
import SendButton from '../Control/SendButton/SendButton';
import { handleSubmit } from './MesHandleSubmit/handleSubmit';
import { messages } from './MesHandleSubmit/handleSubmit';

function Agent() {
  
  return (
    <>
      <div>
        <h1>Chatbot</h1>
        <div id="message-container">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.role}`}>
              {message.content}
            </div>
          ))}
        </div>
        <div>
          <form onSubmit={handleSubmit} className='chat-area'>
            <SendButton handleClick={handleSubmit} />
          </form>
        </div>
      </div>

    </>
  );
}

export default Agent;




