import { data } from "../Data/TodoListData"
import { useState } from "react";
import "./Task.css";

function Task() {
    const [completed, setCompleted] = useState(false);
    const handleCopleted = () => {setCompleted(!completed)}  ;
    return (
        <>
            {data.map(task => (
                <div key={task.id} className="task-item">
                    <h2 className="task-title">{task.description.title}</h2>
                    <div className="task-content">
                        <p className="task-description">Description: {task.description.content}</p>
                        <div className="task-process">
                            <span className="task-status">
                                {(task.process.status) && (<button className="task-status completed" onClick={handleCopleted}>Completed</button>)}
                            </span>
                            <div>
                                <p className="task-duration">Thời gian đã thực hiện: {task.process.actual_duration}h </p>
                                <div className="task-due">Due:
                                    <p className="task-due-date"> <span>{task.date.hour}:{task.date.minute}</span> - {task.date.day}/{task.date.month}/{task.date.year}</p>
                                </div>

                            </div>
                        </div>
                    </div>
                    <button className="task-detail">Detail</button>
                </div>
            ))}
        </>
    )
}
export default Task;
