import { useState } from "react";
import Login from '../Login'

const modalStyle = {
    position: 'fixed', 
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)', 
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "30vh",
        backgroundColor: "rgba(0, 0, 0, 0.5)"
    };

const loginStyle = {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '5px'
};

function Modal() {
    const [statusLogin, setStatusLogin] = useState(false)
    const handleLogin = () => {
        setStatusLogin(true)
    }

    return (
        <>
            {(!statusLogin) && <button onClick={handleLogin}>Đăng kí </button>}
            {(statusLogin) &&
                <div>
                    <div style={loginStyle}>
                        <Login status={statusLogin} setStatusLogin={setStatusLogin} />
                    </div>
                </div>}

        </>
    )
}

export default Modal
