import React, { useState } from "react";

function Login({status, setStatusLogin}) {
    const styleCloseButton = {
        position: "absolute",
        top: "200px",
        right: "100px",
        cursor: "pointer",
        color: "red",
        fontSize: "20px",
        fontWeight: "bold",
        border: "1px solid red",
        borderRadius: "5px",
        padding: "5px",
        backgroundColor: "red",
        color: "white",
        zIndex: "1000",
    }
    
    const inputStyle = {
        padding: "8px",
        margin: "5px 0",
        border: "1px solid #ccc",
        borderRadius: "4px",
        width: "100%"
        };

    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const handleSubmit = (e) => {
        e.preventDefault();

        const user = {
            username,
            password,
        };

        // TODO: Make a request to your API to authenticate the user
    };
    const handleClose = () => {
        setStatusLogin(!status)
    }

    return (
        <>
            <button style={styleCloseButton} onClick={handleClose}>X</button>
            <h1>Login </h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="username">Username</label>
                <input
                    style={inputStyle}
                    type="text"
                    id="username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />

                <label htmlFor="password">Password</label>
                <input
                    style={inputStyle}
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />

                <input type="submit" value="Login" />
            </form>
        </>
    );
}

export default Login;
