import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Menu = () => {
    const arrayMenu = [
        "Trang chủ",
        "Giới thiệu",
        "Sản phẩm",
        "Liên hệ",
        "Đăng ký",
        "Tin tức",
        "Thông tin",
        "Hỗ trợ",
        "Cài đặt",
    ];

  const [hoverIndex, setHoverIndex] = useState(null);

  const MenuList = styled.ul`
    list-style: none;
    padding: 0;
    margin: 0;
    display: flex;
    justify-content: space-around;
    align-items: center;
    background-color: #f2f2f2; 
    border-bottom: 1px solid #ddd;
    padding: 0.5rem 0;
    font-size: 0.9rem;
  `;

  const MenuItem = styled.li`
    padding: 0.5rem 1rem;
    font-weight: 500;
    transition: background-color 0.3s ease;
    &:hover {
      background-color: #e0e0e0; 
    }
  `;

  useEffect(() => {
  const handleMouseOver = (index) => {
    setHoverIndex(index);
};

  const handleMouseOut = () => {
    setHoverIndex(null);
  };

    const menuItems = document.querySelectorAll('.MenuItem'); // Assuming you have a class 'MenuItem' on your MenuItem elements
    menuItems.forEach((item, index) => {
      item.addEventListener('mouseover', () => handleMouseOver(index));
      item.addEventListener('mouseout', handleMouseOut);
    });
    return () => {
      menuItems.forEach((item, index) => {
        item.removeEventListener('mouseover', () => handleMouseOver(index));
        item.removeEventListener('mouseout', handleMouseOut);
      });
};
  }, []);

  return (
    <MenuList>
      {arrayMenu.map((item, index) => (
        <MenuItem
          key={index}
          className="MenuItem" // Add the class name for event listeners
          style={{
            backgroundColor: hoverIndex === index ? '#e0e0e0' : 'transparent',
          }}
        >
          {item}
        </MenuItem>
      ))}
    </MenuList>
  );
};

export default Menu;
