import React, { useState } from 'react';

const AddTodoItem = ({ onAddItem }) => {
    const [newItem, setNewItem] = useState({
        date: { type: 1, day: '', month: '', year: '', hour: '', minute: '' },
        description: { id: 0, title: '', content: '', priority_level: 1 },
        process: { status: false, actual_duration: 0, rate_prosess: 0 },
        analysis: { predicted_delay: 0, predicted_completion: 0, effort_score: '', likelihood_to_complete: 0, classifier: 0 },
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        const [field, subfield] = name.split('.');
        setNewItem((prevItem) => ({
            ...prevItem,
            [field]: { ...prevItem[field], [subfield]: value },
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddItem(newItem);
        setNewItem({
            date: { type: 1, day: '', month: '', year: '', hour: '', minute: '' },
            description: { id: 0, title: '', content: '', priority_level: 1 },
            process: { status: false, actual_duration: 0, rate_prosess: 0 },
            analysis: { predicted_delay: 0, predicted_completion: 0, effort_score: '', likelihood_to_complete: 0, classifier: 0 },
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            {/* Input fields for date, description, process, and analysis */}
            <input type="text" name="date.day" value={newItem.date.day} onChange={handleChange} placeholder="Day" />
            {/* ... other input fields */}
            <button type="submit">Add Item</button>
        </form>
    );
};

export default AddTodoItem;
