import { useState } from "react";
import Login from '../Login'



function Modal() {
    const fromStyle = {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        backgroundColor: "rgba(0, 0, 0, 0.5)"
    };
    const styleCloseButton = {
        position: "absolute",
        top: "50%",
        right: "50%",
        transform: "translate(50%, -50%)",
        border: "none",
        background: "none",
        cursor: "pointer",
        outline: "none"
    }

    const [statusLogin, setStatusLogin] = useState(false)
    const handleLogin = () => {
        setStatusLogin(true)
    }
    const handleClose = () => {
        setStatusLogin(!statusLogin)
    }

    return (
        <>
            {(!statusLogin) && <button onClick={handleLogin}>Đăng kí </button>}
            {(statusLogin) &&
                <div style={fromStyle}>
                    <button style={styleCloseButton} onClick={handleClose}>Close</button>
                    <div>
                        <Login />
                    </div>
                </div>}

        </>
    )
}

export default Modal
