import { format } from 'date-fns';
import './Form.css'
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Form ({ id, task, newItem , setNewItem, HandleCancel, handleSubmit }) {
    const handleChange = (e) => {
        const { name, value } = e.target;
        setNewItem(prevItem => ({
            ...prevItem,
            [name]: value
        }));
    };
    









    const [todoList, setTodoList] = useState([]);

    useEffect(() => {
    // Gọi API lấy danh sách to-do
    axios.get('http://localhost:3006')
        .then(response => {
            setTodoList(response.data);
            console.log(response.data)

            setTodoList(response.data.map(todoList => ({
                    ...todoList,
            })));
        })
        .catch(error => console.error("Lỗi khi tải dữ liệu:", error));
    }, []);
















    const [showAdvanced, setShowAdvanced] = useState(false)
    const showAvaned = () => setShowAdvanced(!showAdvanced)



    return (
        <>
    {todoList.map((item => (

<form onSubmit={handleSubmit} className="add-todo-form">
                <h2>Edit Task</h2>
                <div className="form-group">
                    <label htmlFor="created_at">Time Current:</label>
                    <span className='time-value'>{format((item.created_at), 'dd MMMM yyyy HH:mm')}</span>
                </div>
                <div className="form-group">
                    <label htmlFor="datetime">Date and Time:</label>
                    <input type="datetime-local" name="due_date" value={item.due_date} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="title">Title:</label>
                    <input type="text" name="title" value={item.title} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="content">Content:</label>
                    <textarea name="content" value={item.content} onChange={handleChange} />
                </div>
                <div className='advance-btn'>
                <button type='button' className='advance-button delete'>Xóa nhiệm vụ</button>

                <button type="button" className="advance-button" onClick={showAvaned}>{showAdvanced ? "▲" : "Advance"}</button>
                </div>

            {showAdvanced && (
                <div className='form-advance'>
                    <div className="form-group">
                        <label htmlFor="priority_level">Priority Level:</label>
                        <input type="number" name="priority_level" value={item.priority_level} onChange={handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="status">Status:</label>
                        <input type="checkbox" name="status" checked={item.status} onChange={handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="actual_duration">Actual Duration:</label>
                        <input type="number" name="actual_duration" value={item.actual_duration} onChange={handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="rate_prosess">Rate Process:</label>
                        <input type="number" name="rate_prosess" value={item.rate_prosess} onChange={handleChange} step="0.1" />
                    </div>
                </div>
            )}

                <div className="from-controler">
                    <button type="button" onClick={HandleCancel} className="cancel-button">Cancel</button>
                    <button type="submit" className='submit-cancel-button'>Save</button>
                </div>
                
                

                
            </form>

    )))}
        
        </>
    )
}

export default Form;
