import Task from "./Task";
import AddTask from '../Control/AddTask';
import ChartComponent from './Graph/chart.js';
import ChartComponent1 from './Graph/index.js';
import Agent from '../Agent';
function Note() {

    
    const sizeMax = {
        overflowY: 'auto', /* Add vertical scroll if content exceeds height */
        maxHeight: '500px', /* Set a maximum height for the container  */
    
    }
    return (
        <>
            <div className="note-container ">

                <div className="note-content" style={{ display: 'flex', alignItems: 'center' }}> 
                    <div style={{ ...sizeMax, flex: 2 }} className="task-area">
                        <Task />
                    </div>
                    <div style={{ ...sizeMax, flex: 1, marginLeft: '10px' }} className="task-area">
                        <Agent />
                    </div>
                </div>
            <div style={{ display: 'flex', alignItems: 'center' }}>
                <div>
                <ChartComponent style={{ marginRight: '10px' }} />

                </div>
                <div>
                <ChartComponent1 />

                </div>
                <div>
                <ChartComponent1 />

                </div>
            </div>

            </div>

        </>
    );
}

export default Note;
