import Task from "./Task";
import AddTask from '../Control/AddTask';
import ChartComponent from './Graph/chart.js';
import ChartComponent1 from './Graph/index.js';


function Note() {
    
    const sizeMax = {
        overflowY: 'auto', /* Add vertical scroll if content exceeds height */
        maxHeight: '700px', /* Set a maximum height for the container  */
    
    }
    return (
        <>
            <div className="note-container ">

                <div className="note-content"> 
                    <div style={sizeMax} className="task-area">
                        <Task />
                    </div>
                </div>
            <div style={{ display: 'flex', alignItems: 'center' }}>
                <ChartComponent style={{ marginRight: '10px' }} />
            </div>
            <ChartComponent1 />

            </div>

        </>
    );
}

export default Note;
