// import { data } from "../Data/TodoListData"
import "./Task.css";
import AddTask from '../../Control/AddTask';
import { format } from 'date-fns';
import React, { useState, useEffect } from 'react';
import axios from 'axios';


function Task() {
    const data = []
    const [todoList, setTodoList] = useState(data);
    console.log(todoList)

    const [todos, setTodos] = useState([]);

    useEffect(() => {
    // Gọi API lấy danh sách to-do
    axios.get('http://localhost:3006')
        .then(response => {
            setTodos(response.data);
        })
        .catch(error => console.error("Lỗi khi tải dữ liệu:", error));
    }, []);

    const handleAddItem = async (newItem) => {
        console.log(newItem)

        const response = await fetch('http://localhost:3006', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/x-www-form-urlencoded',
            },
            // body: `prompt=${"output để dạng JSON"}${newItem}`,
            // body: JSON.stringify(newItem), // Sử dụng JSON.stringify vif lúc train model data mình để dạng string nên newItem ỏ đây model không hiểu

          });
          const data = await response.json();
          setTodoList(data);
          console.log(data)
        


        // const responsef = await fetch('http://localhost:3006', {
        // method: 'POST',
        // headers: {
        //     'Content-Type': 'application/x-www-form-urlencoded',
        // },
        // // body: `prompt=${"output để dạng JSON"}${newItem}`,
        // body: (newItem), // Sử dụng JSON.stringify vif lúc train model data mình để dạng string nên newItem ỏ đây model không hiểu

        // });
        // const datasend = await responsef.json();
        // console.log(datasend)
        

        setTodoList([...todoList, { ...newItem, id: todoList.length + 1 }]);

    };


    return (
        <div className="note">
            <div className="note-container">
                {/* <AddTodoItem onAddItem={handleAddItem} />  */}
                <AddTask handleItem={handleAddItem} />
            </div>
            {todoList.map(task => (
                <div key={task.id} className="task-item">
                    <div style={{ width: '100%', height: '100%' }} className="task-content">
                        <h2 className="task-title"> Title: {task.title}</h2>
                        <div className="task-process">
                            <span>
                                <p className="task-status completed"> {task.status ? "Completed" : "In Progress"} </p>
                            </span>
                        </div>
                    </div>
                    <div className="task-content">
                        <div style={{ width: '100%' }}>
                            <p className="task-description">Description: {task.content}</p>
                        </div>
                    </div>


                    <div className="task-time">
                        <div className="task-due">Due:
                            <p className="task-due-date" style={{ fontSize: '1rem', fontWeight: '500' }}>{format(new Date(), 'dd MMMM yyyy HH:mm')}</p>
                        </div>
                        <p className="task-duration">Thời gian đã thực hiện: {task.actual_duration}h </p>

                        <button className="task-detail">Detail</button>

                    </div>


                </div>
            ))}
        </div>
    )
}
export default Task;
