import React, { useState } from 'react';

const AddTodoItem = ({ onAddItem }) => {
    const [newItem, setNewItem] = useState({
        date: { type: 1, day: '', month: '', year: '', hour: '', minute: '' },
        description: { id: 0, title: '', content: '', priority_level: 1 },
        process: { status: false, actual_duration: 0, rate_prosess: 0 },
        analysis: { predicted_delay: 0, predicted_completion: 0, effort_score: '', likelihood_to_complete: 0, classifier: 0 },
    });

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        const [field, subfield] = name.split('.');
        setNewItem((prevItem) => ({
            ...prevItem,
            [field]: {
                ...prevItem[field],
                [subfield]: type === 'checkbox' ? checked : value,
            },
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddItem(newItem);
        setNewItem({
            date: { type: 1, day: '', month: '', year: '', hour: '', minute: '' },
            description: { id: 0, title: '', content: '', priority_level: 1 },
            process: { status: false, actual_duration: 0, rate_prosess: 0 },
            analysis: { predicted_delay: 0, predicted_completion: 0, effort_score: '', likelihood_to_complete: 0, classifier: 0 },
        });
    };

    return (
        <form onSubmit={handleSubmit} className="add-todo-form">
            <h2>Add Todo Item</h2>
            <div className="form-group">
            <label htmlFor="date.day">Day:</label>
                <input type="text" id="date.day" name="date.day" value={newItem.date.day} onChange={handleChange} />
            </div>
            <div className="form-group">
            <label htmlFor="date.month">Month:</label>
                <input type="text" id="date.month" name="date.month" value={newItem.date.month} onChange={handleChange} />
            </div>
            <div className="form-group">
            <label htmlFor="date.year">Year:</label>
                <input type="text" id="date.year" name="date.year" value={newItem.date.year} onChange={handleChange} />
            </div>
            {/* ... other input fields similarly styled */}
            <button type="submit" className="submit-button">Add Item</button>
        </form>
    );
};

export default AddTodoItem;
