import React, { useState } from 'react';
import { format } from 'date-fns';
import Form from './Form/Form';

function Detail({ id, task }) {
    // phần này đang sửa để kéo giá trị đã có từ db để người dùng sửa tiếp
    const [newItem, setNewItem] = useState({
        created_at: new Date(),
        due_date: '',
        title: '',
        content: ''
    });
    const [errors, setErrors] = useState({});

// hàm dưới này dùng cách tạo hàm trống để bên form gọi và dùng chỉnh sửa tùy form và fetch api mai chỉnh lại cả đây và bên form
    const handleSubmit = (e, newItem) => {
        e.preventDefault();
        let isValid = true;
        if (isValid) {
            // onAddItem(newItem);
            // setTodoList([...todoList, newItem]);
            setNewItem({
                id: 0,
                due_date: newItem.due_date,
                created_at: newItem.created_at,
                title: '',
                content: '',
                status: newItem.status,
            });
        }
    };

    const HandleCancelForm = () => {
        // Add your cancel logic here
    };
    return (
        <>
            {/* <Form newItem={newItem} id={id} task={task} setNewItem={setNewItem} HandleCancelForm={HandleCancelForm} handleSubmit={handleSubmit}/> */}
        </>
    );
}

export default Detail;


