import "./Task.css";
function Task({ task }) {
    console.log(task)
  const { description, date, process } = task;
  return (
    <div className="task-item" key={task.id}>
                    <div className="task-content">
        <div>
          <h2 className="task-title">Title: {description.title}</h2>
          <p className="task-description">Description: {description.content}</p>
                        </div>

                        <div className="task-process">
                            <span>
            <p className="task-status completed">Completed</p>
                            </span>
                        </div>
                    </div>

                    <div className="task-time">
        <div className="task-due">
          Due:
          <p className="task-due-date">
            <span>
              {date.hour}:{date.minute}
            </span>{" "}
            - {date.day}/{date.month}/{date.year}
          </p>
                        </div>
        <p className="task-duration">
          Thời gian đã thực hiện: {process.actual_duration}h
        </p>

                        <button className="task-detail">Detail</button>
                    </div>
                </div>
  );
}

export default Task;
