import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import Form from './Form/Form';

function Detail({ id, task, todoList, HandleCancel }) {
    const [newItem, setNewItem] = useState({
        id: 0,
        created_at: new Date(),
        due_date: new Date(),
        title: '',
        content: '',
        status: '',
        rate_prosess: 0,
        actual_duration: 0,
    });

    useEffect(() => {
        const currentTask = todoList.find(item => item.id === id);
        if (currentTask) {
            setNewItem({
                id: currentTask.id,
                created_at: new Date(currentTask.created_at),
                due_date: new Date(currentTask.due_date),
                title: currentTask.title,
                content: currentTask.content,
                status: currentTask.status,
                rate_prosess: currentTask.rate_prosess || 0,
                actual_duration: currentTask.actual_duration || 0,
            });
        }
    }, [id, todoList]);
    const [errors, setErrors] = useState({});

    const handleSubmit = (e, newItem) => {
        e.preventDefault();
        let isValid = true;
        if (isValid) {
            console.log('Submitting updated task:', newItem);
        }
    };

    return (
        <>
            <Form 
                newItem={newItem} 
                handleSubmit={handleSubmit} 
                id={id} 
                task={task} 
                setNewItem={setNewItem} 
                HandleCancel={HandleCancel}
            />
        </>
    );
}

export default Detail;



