import { useState } from "react";
import Login from '../Login'

const modalStyle = {
    position: 'fixed',
    position: 'fixed', 
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    transform: 'translate(-50%, -50%)', 
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "30vh",
        backgroundColor: "rgba(0, 0, 0, 0.5)"
    };

const loginStyle = {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '5px'
};

const buttonStyle = {
    backgroundColor: '#4CAF50', /* Green */
    border: 'none',
    color: 'white',
    padding: '10px 20px',
    textAlign: 'center',
    textDecoration: 'none',
    display: 'inline-block',
    fontSize: '16px',
    borderRadius: '5px',
    cursor: 'pointer'
};

function Modal() {
    const [statusLogin, setStatusLogin] = useState(false)
    const handleLogin = () => {
        setStatusLogin(true)
    }

    return (
        <>
            {(true) && <button style={buttonStyle} onClick={handleLogin}>Đăng kí </button>}
            {(statusLogin) &&
                <div style={modalStyle}>
                    <div style={loginStyle}>
                        <Login status={statusLogin} setStatusLogin={setStatusLogin} />
                    </div>
                </div>}

        </>
    )
}

export default Modal
